  const clearFilters = () => {
    setSearchQuery("");
    setStatusFilter("all");
    setOSFilter("all");
    setLocationFilter("all");
    setRiskFilter("all");
    setPrivilegeFilter("all");
  };

  return (
    <div className="space-y-6 p-6" data-testid="clients-page">
