/* C2 Command & Control Navbar Styles */
/* Terminal Status Bar */
.c2-status-bar {
  position: fixed;
  top: 0;
  width: 100%;
  background: #1a1a1a;
  border-bottom: 1px solid #333;
  padding: 8px 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  font-size: 12px;
  z-index: 1001;
  height: 40px;
  font-family: 'JetBrains Mono', monospace;
}

.status-left {
  display: flex;
  align-items: center;
  gap: 20px;
}

.terminal-prompt {
  color: #00ff00;
  font-weight: 600;
}

.status-indicator {
  display: flex;
  align-items: center;
  gap: 5px;
  color: #00ff00;
}

.status-indicator i {
  color: #00ff00;
  animation: pulse 2s infinite;
}

@keyframes pulse {
  0%, 50%, 100% { opacity: 1; }
  25%, 75% { opacity: 0.5; }
}

.status-right {
  display: flex;
  align-items: center;
  gap: 20px;
  color: #888;
}

.session-info {
  color: #00ff00;
}

.timestamp {
  color: #666;
}

/* C2 Navigation Bar */
.c2-navbar {
  position: fixed;
  top: 40px;
  width: 100%;
  background: rgba(10, 10, 10, 0.95);
  backdrop-filter: blur(10px);
  border-bottom: 2px solid #00ff00;
  z-index: 1100;
  transition: all 0.3s ease;
  box-shadow: 0 0 20px rgba(0, 255, 0, 0.1);
  font-family: 'JetBrains Mono', monospace;
}

.c2-navbar.scrolled {
  background: rgba(10, 10, 10, 0.98);
  box-shadow: 0 0 30px rgba(0, 255, 0, 0.2);
}

.nav-container {
  max-width: 100vw;
  margin: 0;
  padding: 0 10px;
  display: grid;
  grid-template-columns: minmax(200px, 250px) 1fr minmax(250px, 300px);
  align-items: center;
  height: 60px;
  gap: 10px;
  overflow: visible;
  position: relative;
}

/* Three Column Layout */
.nav-left {
  display: flex;
  align-items: center;
  justify-content: flex-start;
}

.nav-center {
  display: flex;
  align-items: center;
  justify-content: center;
}

.nav-right {
  display: flex;
  align-items: center;
  justify-content: flex-end;
  gap: 20px;
}

/* C2 Logo */
.nav-logo {
  display: flex;
  align-items: center;
  gap: 12px;
  text-decoration: none;
  transition: all 0.3s ease;
}

.nav-logo:hover {
  opacity: 0.8;
}

.logo-icon {
  font-size: 24px;
  color: #00ff00;
  text-shadow: 0 0 10px rgba(0, 255, 0, 0.5);
}

.logo-text {
  display: flex;
  flex-direction: column;
}

.brand-name {
  font-size: 18px;
  font-weight: 700;
  color: #00ff00;
  text-shadow: 0 0 5px rgba(0, 255, 0, 0.3);
}

.version {
  font-size: 10px;
  color: #666;
  margin-top: -2px;
}

/* Navigation Actions */
.nav-actions {
  display: flex;
  align-items: center;
  gap: 10px;
}

/* Navigation Menu */
.nav-menu {
  display: flex;
  align-items: center;
  gap: 0;
  flex-wrap: nowrap;
  overflow: visible;
  justify-content: center;
  width: 100%;
}

.nav-link {
  display: flex;
  align-items: center;
  gap: 6px;
  text-decoration: none;
  color: #888;
  font-weight: 500;
  font-size: 12px;
  padding: 10px 12px;
  position: relative;
  transition: all 0.3s ease;
  border-left: 1px solid #333;
  min-width: 90px;
  max-width: 140px;
  justify-content: center;
  white-space: nowrap;
  flex-shrink: 1;
  overflow: hidden;
  text-overflow: ellipsis;
}

.nav-link:first-child {
  border-left: none;
}

.nav-link:hover {
  color: #00ff00;
  background: rgba(0, 255, 0, 0.05);
  text-shadow: 0 0 5px rgba(0, 255, 0, 0.3);
}

.nav-link.active {
  color: #00ff00;
  background: rgba(0, 255, 0, 0.1);
  text-shadow: 0 0 5px rgba(0, 255, 0, 0.5);
}

.nav-link i {
  font-size: 14px;
}

.badge {
  background: #ff4444;
  color: #000;
  font-size: 10px;
  padding: 2px 6px;
  border-radius: 10px;
  font-weight: bold;
  margin-left: 5px;
}

.badge.small {
  font-size: 8px;
  padding: 1px 4px;
}

/* Dropdown Styles */
.nav-dropdown {
  position: relative;
  display: inline-block;
}

.dropdown-trigger {
  cursor: pointer;
  user-select: none;
}

.dropdown-arrow {
  font-size: 10px;
  transition: transform 0.3s ease;
  margin-left: 5px;
}

.nav-dropdown:hover .dropdown-arrow,
.nav-dropdown.active .dropdown-arrow,
.dropdown-arrow.rotate {
  transform: rotate(180deg);
}

.dropdown-menu {
  position: absolute;
  top: 100%;
  left: 0;
  background: rgba(10, 10, 10, 0.98);
  backdrop-filter: blur(10px);
  border: 1px solid #333;
  border-top: 2px solid #00ff00;
  border-radius: 0 0 8px 8px;
  min-width: 220px;
  z-index: 9999;
  opacity: 0;
  visibility: hidden;
  transform: translateY(-10px);
  transition: all 0.3s ease;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.5);
  pointer-events: none;
}

/* Desktop dropdown behavior */
@media screen and (min-width: 1025px) {
  .nav-dropdown:hover .dropdown-menu,
  .nav-dropdown.active .dropdown-menu,
  .dropdown-menu.show {
    opacity: 1 !important;
    visibility: visible !important;
    transform: translateY(0) !important;
    pointer-events: auto !important;
  }
}

/* All screens dropdown click behavior */
.nav-dropdown.active .dropdown-menu,
.dropdown-menu.show {
  opacity: 1;
  visibility: visible;
  transform: translateY(0);
  pointer-events: auto;
}

.dropdown-item {
  display: flex;
  align-items: center;
  gap: 10px;
  padding: 12px 16px;
  color: #888;
  text-decoration: none;
  font-size: 12px;
  transition: all 0.3s ease;
  border-bottom: 1px solid #222;
  position: relative;
}

.dropdown-item:last-child {
  border-bottom: none;
}

.dropdown-item:hover {
  color: #00ff00;
  background: rgba(0, 255, 0, 0.05);
  text-shadow: 0 0 5px rgba(0, 255, 0, 0.3);
  padding-left: 20px;
}

.dropdown-item i {
  font-size: 12px;
  width: 16px;
  text-align: center;
  color: #666;
}

.dropdown-item:hover i {
  color: #00ff00;
}

.dropdown-item .badge {
  margin-left: auto;
  background: #00ff00;
  color: #000;
  font-size: 8px;
  padding: 1px 4px;
}

.dropdown-divider {
  height: 1px;
  background: #333;
  margin: 8px 0;
}

/* Dropdown positioning for different menu items */
.nav-dropdown:nth-child(2) .dropdown-menu { left: 0; }
.nav-dropdown:nth-child(3) .dropdown-menu { left: -50px; }
.nav-dropdown:nth-child(4) .dropdown-menu { left: -100px; }
.nav-dropdown:nth-child(5) .dropdown-menu { left: -150px; }
.nav-dropdown:nth-child(6) .dropdown-menu { left: -200px; }
.nav-dropdown:nth-child(7) .dropdown-menu { left: -250px; }

/* Dropdown item hover effects */
.dropdown-item::before {
  content: '';
  position: absolute;
  left: 0;
  top: 0;
  height: 100%;
  width: 3px;
  background: #00ff00;
  transform: scaleY(0);
  transition: transform 0.3s ease;
}

.dropdown-item:hover::before {
  transform: scaleY(1);
}

/* Special effects for different categories */
.nav-dropdown:nth-child(2) .dropdown-menu { border-top-color: #00ff00; } /* Sessions */
.nav-dropdown:nth-child(3) .dropdown-menu { border-top-color: #ff6b35; } /* Payloads */
.nav-dropdown:nth-child(4) .dropdown-menu { border-top-color: #ff4444; } /* Exploits */
.nav-dropdown:nth-child(5) .dropdown-menu { border-top-color: #00aaff; } /* Modules */
.nav-dropdown:nth-child(6) .dropdown-menu { border-top-color: #ffaa00; } /* Listeners */
.nav-dropdown:nth-child(7) .dropdown-menu { border-top-color: #34a853; } /* Reports */

/* System Status */
.system-status {
  display: flex;
  align-items: center;
  gap: 8px;
  font-size: 9px;
  padding: 6px 8px;
  background: rgba(0, 255, 0, 0.05);
  border: 1px solid rgba(0, 255, 0, 0.1);
