}

.bar {
  width: 20px;
  height: 2px;
  background: #00ff00;
  margin: 2px 0;
  transition: 0.3s;
  box-shadow: 0 0 5px rgba(0, 255, 0, 0.3);
}

.nav-toggle.active .bar:nth-child(2) {
  opacity: 0;
}

.nav-toggle.active .bar:nth-child(1) {
  transform: translateY(6px) rotate(45deg);
}

.nav-toggle.active .bar:nth-child(3) {
  transform: translateY(-6px) rotate(-45deg);
}

/* Adjust main content for fixed navbar */
main {
  margin-top: 100px; /* 40px status bar + 60px navbar */
  width: 100%;
  box-sizing: border-box;
}

/* Ensure full width layout */
* {
  box-sizing: border-box;
}

body {
  margin: 0;
  padding: 0;
  width: 100%;
  overflow-x: hidden;
}

/* Responsive Design */
@media screen and (max-width: 1400px) {
  .nav-container {
    grid-template-columns: 200px 1fr 250px;
    padding: 0 8px;
  }

  .nav-link {
    font-size: 11px;
    padding: 10px 8px;
    min-width: 85px;
    max-width: 115px;
  }

  .system-status {
    gap: 6px;
    font-size: 9px;
    padding: 5px 6px;
  }
}

@media screen and (max-width: 1280px) {
  .nav-toggle {
    display: flex;
  }

  .nav-container {
    grid-template-columns: 1fr auto;
    gap: 10px;
    padding: 0 10px;
  }

  .nav-center {
    position: fixed;
    top: 100px; /* Below the status bar and navbar */
    left: 0;
    right: 0;
    background: rgba(10, 10, 10, 0.98);
    backdrop-filter: blur(10px);
    border-top: 2px solid #00ff00;
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.5);
    transform: translateY(-100%);
    opacity: 0;
    visibility: hidden;
    transition: all 0.3s ease;
    z-index: 1000;
    padding: 20px;
    max-height: calc(100vh - 100px);
    overflow-y: auto;
  }

  .nav-center.mobile-open {
    transform: translateY(0);
    opacity: 1;
    visibility: visible;
  }

  .nav-menu {
    flex-direction: column;
    gap: 0;
    width: 100%;
    overflow: hidden;
  }

  .nav-dropdown {
    width: 100%;
    border-bottom: 1px solid #333;
  }

  .nav-link {
    font-size: 16px;
    padding: 16px 20px;
    min-width: auto;
    max-width: none;
    width: 100%;
    justify-content: flex-start;
    border-left: none;
    border-bottom: 1px solid #222;
  }

  .dropdown-menu {
    position: static;
    opacity: 0;
    visibility: hidden;
    transform: none;
    background: rgba(0, 0, 0, 0.4);
    border: none;
    border-radius: 0;
    box-shadow: inset 0 2px 10px rgba(0, 0, 0, 0.3);
    margin-top: 0;
    pointer-events: none;
    min-width: auto;
  }

  .nav-dropdown.active .dropdown-menu {
    opacity: 1;
    visibility: visible;
    pointer-events: auto;
  }

  .dropdown-item {
    font-size: 14px;
    padding: 14px 30px;
  }

  .mobile-nav-actions {
    display: block;
    border-top: 2px solid #333;
    margin-top: 10px;
    padding-top: 10px;
  }

  .nav-right {
    display: none;
  }
}

@media screen and (max-width: 768px) {
  .nav-container {
    grid-template-columns: 1fr;
    height: auto;
    padding: 8px 10px;
    gap: 10px;
  }

  .nav-left {
    justify-content: flex-start;
  }

  .nav-toggle {
    display: flex;
  }

  .nav-right {
    display: none;
  }

  .system-status {
    display: none;
  }

  /* Logo adjustments for mobile */
  .logo-text .version {
    display: none;
  }

  .brand-name {
    font-size: 14px;
  }
}

