export default function LateralMovementPage() {
  const [searchQuery, setSearchQuery] = useState("");
  const { toast } = useToast();

  const { data: modulesData, isLoading } = useQuery({
    queryKey: ["modules", "lateral"],
    queryFn: async () => {
      const response = await fetch("/api/modules/category/lateral");
      if (!response.ok) throw new Error("Failed to fetch lateral movement modules");
      return response.json();
    }
  });

  const modules = modulesData?.data || [];

  const filteredModules = modules.filter((module: any) => 
    module.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    module.description.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const handleAddModule = () => {
    toast({
      title: "Add Module",
      description: "Module creation dialog will be implemented soon"
    });
  };

  const handleDeployModule = (moduleId: string, moduleName: string) => {
    toast({
      title: "Module Deployed",
      description: `${moduleName} has been deployed successfully`
    });
  };

  const handleConfigureModule = (moduleId: string, moduleName: string) => {
    toast({
      title: "Configure Module",
      description: `Configuration for ${moduleName} will be implemented soon`
    });
  };

  return (
    <div className="container-responsive py-6">
      <div className="flex items-center justify-between mb-6">
        <div>
          <h1 className="text-2xl font-bold tracking-tight text-blue-400">Lateral Movement</h1>
          <p className="text-muted-foreground">Network traversal and lateral movement techniques</p>
        </div>
        <Button className="bg-blue-600 hover:bg-blue-700" onClick={handleAddModule} data-testid="button-add-module">
          <Plus className="h-4 w-4 mr-2" />
          Add Module
        </Button>
      </div>

      {/* Search and Filter */}
      <div className="flex items-center gap-4 mb-6">
        <div className="relative flex-1">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground h-4 w-4" />
          <Input
            placeholder="Search modules..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="pl-10"
            data-testid="input-search-modules"
          />
        </div>
        <Button variant="outline" size="icon" data-testid="button-filter">
          <Filter className="h-4 w-4" />
        </Button>
      </div>

      {/* Loading State */}
      {isLoading && (
        <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 gap-6">
          {[1, 2, 3].map((i) => (
            <Card key={i}>
              <CardHeader>
                <Skeleton className="h-4 w-3/4" />
                <Skeleton className="h-3 w-full" />
              </CardHeader>
              <CardContent>
                <Skeleton className="h-20 w-full mb-4" />
                <div className="flex gap-2 mb-4">
                  <Skeleton className="h-6 w-16" />
                  <Skeleton className="h-6 w-16" />
                </div>
                <Skeleton className="h-8 w-full" />
              </CardContent>
            </Card>
          ))}
        </div>
      )}

      {/* Empty State */}
      {!isLoading && filteredModules.length === 0 && searchQuery === "" && (
        <div className="text-center py-12">
          <ArrowRight className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
          <h3 className="text-lg font-semibold mb-2">No Lateral Movement Modules</h3>
          <p className="text-muted-foreground mb-4">Get started by adding your first lateral movement module</p>
          <Button variant="outline" onClick={handleAddModule} data-testid="button-add-first-module">
            <Plus className="h-4 w-4 mr-2" />
            Add Module
          </Button>
        </div>
      )}

      {/* No Search Results */}
      {!isLoading && filteredModules.length === 0 && searchQuery !== "" && (
