                  <div className="flex-1">
                    <CardTitle className="text-lg font-semibold text-blue-400 mb-1" data-testid={`text-module-name-${module.id}`}>
                      {module.name}
                    </CardTitle>
                    <div className="flex items-center gap-2 mb-2">
                      {module.reliable ? (
                        <CheckCircle className="h-4 w-4 text-green-500" />
                      ) : (
                        <AlertTriangle className="h-4 w-4 text-yellow-500" />
                      )}
                      <span className="text-xs text-muted-foreground">
                        {module.reliable ? "Reliable" : "Experimental"}
                      </span>
                    </div>
                  </div>
                </div>
                <CardDescription className="text-sm" data-testid={`text-module-description-${module.id}`}>
                  {module.description}
                </CardDescription>
              </CardHeader>
              <CardContent className="pt-0">
