                  <Label className="text-sm font-semibold">Description</Label>
                  <p className="text-sm text-muted-foreground mt-1">
                    {payload.description || "Mobile application payload for penetration testing and red team operations."}
                  </p>
                </div>
                <div>
                  <Label className="text-sm font-semibold">Configuration</Label>
                  <div className="mt-2 p-3 bg-muted/50 rounded-lg">
                    <pre className="text-xs">{JSON.stringify(payload.config, null, 2)}</pre>
                  </div>
                </div>
                <div>
                  <Label className="text-sm font-semibold">Created</Label>
                  <p className="text-sm text-muted-foreground">{new Date(payload.createdAt).toLocaleString()}</p>
                </div>
              </div>
            </DialogContent>
          </Dialog>
        </div>
      </CardContent>
    </Card>
  );
}

export default function MobilePayloadsPage() {
  const [searchQuery, setSearchQuery] = useState("");
  const [typeFilter, setTypeFilter] = useState<string>("all");
  const [, navigate] = useLocation();

  const { data: payloadsData, isLoading } = useQuery({
    queryKey: ["payloads", "mobile"],
    queryFn: async () => {
      const response = await fetch("/api/payloads/platform/mobile");
      if (!response.ok) throw new Error("Failed to fetch mobile payloads");
      return response.json();
    }
  });

  const payloads = payloadsData?.data || [];

  const filteredPayloads = payloads.filter((payload: MobilePayload) => {
    const matchesSearch = payload.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         payload.description?.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesType = typeFilter === "all" || payload.type === typeFilter;
    return matchesSearch && matchesType;
  });

  return (
    <div className="container-responsive py-6">
      <div className="flex items-center justify-between mb-6">
        <div>
          <h1 className="text-2xl font-bold tracking-tight text-cyan-400">Mobile Payloads</h1>
          <p className="text-muted-foreground">Android and iOS applications and exploits</p>
        </div>
        <Button 
          className="bg-cyan-600 hover:bg-cyan-700" 
          data-testid="button-generate-payload"
          onClick={() => navigate("/payloads/generator?platform=mobile")}
        >
          <Plus className="h-4 w-4 mr-2" />
          Generate Payload
        </Button>
      </div>

      <div className="flex flex-col sm:flex-row gap-4 mb-6">
        <div className="relative flex-1">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
          <Input
            placeholder="Search mobile payloads..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="pl-10"
            data-testid="input-search-payloads"
          />
        </div>
        <div className="flex gap-2">
          <Select value={typeFilter} onValueChange={setTypeFilter}>
            <SelectTrigger className="w-[130px]">
              <Filter className="h-4 w-4 mr-2" />
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Types</SelectItem>
              <SelectItem value="apk">APK</SelectItem>
              <SelectItem value="ipa">IPA</SelectItem>
              <SelectItem value="script">Script</SelectItem>
            </SelectContent>
          </Select>
        </div>
      </div>

      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
        {isLoading ? (
          Array.from({ length: 6 }).map((_, i) => (
            <Card key={i} className="bg-card/50 border-border/50 animate-pulse">
              <CardHeader>
                <div className="h-4 bg-muted rounded w-32"></div>
                <div className="h-3 bg-muted rounded w-24"></div>
              </CardHeader>
              <CardContent>
                <div className="space-y-2">
                  <div className="h-3 bg-muted rounded w-full"></div>
                  <div className="h-3 bg-muted rounded w-3/4"></div>
                </div>
              </CardContent>
            </Card>
          ))
        ) : filteredPayloads.length > 0 ? (
          filteredPayloads.map((payload: MobilePayload) => (
            <PayloadCard key={payload.id} payload={payload} />
          ))
        ) : (
          <div className="col-span-full text-center py-12">
            <Smartphone className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
            <h3 className="text-lg font-semibold mb-2">No mobile payloads found</h3>
            <p className="text-muted-foreground mb-4">
              {searchQuery || typeFilter !== "all" 
                ? "No payloads match your current filters." 
                : "No mobile payloads are available."}
            </p>
            <Button variant="outline" data-testid="button-generate-payload-empty">
              <Plus className="h-4 w-4 mr-2" />
              Generate Payload
            </Button>
          </div>
        )}
      </div>

      {!isLoading && filteredPayloads.length > 0 && (
        <div className="mt-6 text-center text-sm text-muted-foreground">
          Showing {filteredPayloads.length} mobile payloads
        </div>
      )}
    </div>
  );
}

client/src/pages/payloads/PayloadGenerat.content:
import { useState, useEffect } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { SEO } from "@/components/SEO";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { Switch } from "@/components/ui/switch";
import { Separator } from "@/components/ui/separator";
import { Wand2, Download, Settings, Play, Code, Shield } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { useMutation } from "@tanstack/react-query";
import { queryClient } from "@/lib/queryClient";

const platforms = [
  { id: "windows", name: "Windows", color: "text-blue-400" },
  { id: "linux", name: "Linux", color: "text-orange-400" },
  { id: "macos", name: "macOS", color: "text-gray-400" },
  { id: "web", name: "Web", color: "text-purple-400" },
  { id: "mobile", name: "Mobile", color: "text-cyan-400" }
];

const payloadTypes = {
  windows: ["executable", "dll", "script", "shellcode"],
  linux: ["executable", "script", "shellcode"],
  macos: ["application", "script"],
  web: ["webshell", "script"],
  mobile: ["apk", "ipa"]
};

export default function PayloadGeneratorPage() {
  const [platform, setPlatform] = useState("");
  const [payloadType, setPayloadType] = useState("");
  const [payloadName, setPayloadName] = useState("");
  const [lhost, setLhost] = useState("192.168.1.100");
  const [lport, setLport] = useState("4444");
  const [architecture, setArchitecture] = useState("x64");
  const [encoder, setEncoder] = useState("none");
  const [iterations, setIterations] = useState("1");
  const [obfuscation, setObfuscation] = useState(false);
  const [persistence, setPersistence] = useState(false);
  const [antiVirus, setAntiVirus] = useState(false);
  const [customCode, setCustomCode] = useState("");
  const [generatedPayload, setGeneratedPayload] = useState<any>(null);

  const { toast } = useToast();

  // Handle URL parameters for platform pre-selection
  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const platformParam = urlParams.get('platform');
    if (platformParam && platforms.find(p => p.id === platformParam)) {
      setPlatform(platformParam);
      setPayloadName(`${platformParam}_payload_${Date.now()}`);
    }
  }, []);

  const generateMutation = useMutation({
    mutationFn: async (payloadConfig: any) => {
      const response = await fetch('/api/payloads/generate', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payloadConfig)
      });
      if (!response.ok) throw new Error('Generation failed');
      return response.json();
    },
    onSuccess: (data) => {
      setGeneratedPayload(data.data);
      toast({
        title: "Payload Generated Successfully",
        description: `${payloadName} is ready for download`,
      });
      // Invalidate payloads cache to refresh payload lists
      queryClient.invalidateQueries({ queryKey: ['payloads', platform] });
    },
    onError: () => {
      toast({
        title: "Generation Failed",
        description: "Failed to generate payload. Please try again.",
        variant: "destructive"
      });
    }
  });

  const downloadMutation = useMutation({
    mutationFn: async (payloadId: string) => {
      const response = await fetch(`/api/payloads/${payloadId}/download`, {
        method: 'POST'
      });
      if (!response.ok) throw new Error('Download failed');
      return { payloadId };
    },
    onSuccess: ({ payloadId }) => {
      // Trigger file download
      const link = document.createElement('a');
      link.href = `/api/payloads/${payloadId}/download`;
      link.download = `${payloadName}.${getFileExtension()}`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      toast({
        title: "Download Started",
        description: `${payloadName} download initiated successfully.`
      });
    },
    onError: () => {
      toast({
        title: "Download Failed",
        description: "Failed to download payload. Please try again.",
        variant: "destructive"
      });
    }
  });

  const handleGenerate = () => {
    if (!platform || !payloadType || !payloadName) {
      toast({
        title: "Validation Error",
        description: "Please fill in all required fields",
        variant: "destructive"
      });
      return;
    }

    const payloadConfig = {
      platform,
      type: payloadType,
      name: payloadName,
      config: {
        host: lhost,
        port: parseInt(lport),
        architecture,
        encoder: encoder !== 'none' ? encoder : null,
        iterations: encoder !== 'none' ? parseInt(iterations) : 1,
        obfuscation,
        persistence,
        antiVirus,
        customCode: customCode.trim() || null
      }
    };

    generateMutation.mutate(payloadConfig);
  };

  const handleDownload = () => {
    if (generatedPayload?.id) {
      downloadMutation.mutate(generatedPayload.id);
    }
  };

  const getFileExtension = () => {
    switch (platform) {
      case 'windows': return payloadType === 'dll' ? 'dll' : 'exe';
      case 'linux': return 'elf';
      case 'macos': return 'app';
      case 'web': return payloadType === 'webshell' ? 'php' : 'js';
      case 'mobile': return payloadType;
      default: return 'bin';
    }
  };

  const selectedPlatform = platforms.find(p => p.id === platform);
  const availableTypes = platform ? payloadTypes[platform as keyof typeof payloadTypes] : [];

  return (
    <>
      <SEO 
        title="Payload Generator - C2 Command & Control"
        description="Generate custom payloads for different platforms including Windows, Linux, macOS, web and mobile with advanced evasion features"
        keywords="payload generator, pentesting, red team, c2, command control"
      />
      <div className="container-responsive py-6 max-w-6xl">
        <div className="mb-6">
        <h1 className="text-2xl font-bold tracking-tight text-green-400">Payload Generator</h1>
        <p className="text-muted-foreground">Generate custom payloads for different platforms and scenarios</p>
      </div>

      <div className="grid gap-6 lg:grid-cols-3">
        <div className="lg:col-span-2 space-y-6">
          <Card>
            <CardHeader>
              <CardTitle>Basic Configuration</CardTitle>
              <CardDescription>Select platform and payload type</CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid gap-4 sm:grid-cols-2">
                <div>
                  <Label htmlFor="platform">Target Platform</Label>
                  <Select value={platform} onValueChange={setPlatform}>
                    <SelectTrigger>
                      <SelectValue placeholder="Select platform..." />
                    </SelectTrigger>
                    <SelectContent>
                      {platforms.map((p) => (
                        <SelectItem key={p.id} value={p.id}>
                          <span className={p.color}>{p.name}</span>
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <Label htmlFor="type">Payload Type</Label>
                  <Select value={payloadType} onValueChange={setPayloadType} disabled={!platform}>
                    <SelectTrigger>
                      <SelectValue placeholder="Select type..." />
                    </SelectTrigger>
                    <SelectContent>
                      {availableTypes.map((type) => (
                        <SelectItem key={type} value={type}>
                          {type}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <div>
                <Label htmlFor="name">Payload Name</Label>
                <Input
                  id="name"
                  value={payloadName}
                  onChange={(e) => setPayloadName(e.target.value)}
                  placeholder="Enter payload name..."
                />
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle>Network Configuration</CardTitle>
              <CardDescription>Configure connection parameters</CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid gap-4 sm:grid-cols-2">
                <div>
                  <Label htmlFor="lhost">Listen Host (LHOST)</Label>
                  <Input
                    id="lhost"
                    value={lhost}
                    onChange={(e) => setLhost(e.target.value)}
                    placeholder="192.168.1.100"
                  />
                </div>

                <div>
                  <Label htmlFor="lport">Listen Port (LPORT)</Label>
                  <Input
                    id="lport"
                    type="number"
                    value={lport}
                    onChange={(e) => setLport(e.target.value)}
                    placeholder="4444"
                  />
                </div>
              </div>

              <div className="grid gap-4 sm:grid-cols-2">
                <div>
                  <Label htmlFor="architecture">Architecture</Label>
                  <Select value={architecture} onValueChange={setArchitecture}>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="x86">x86 (32-bit)</SelectItem>
                      <SelectItem value="x64">x64 (64-bit)</SelectItem>
                      <SelectItem value="arm">ARM</SelectItem>
                      <SelectItem value="arm64">ARM64</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <Label htmlFor="encoder">Encoder</Label>
                  <Select value={encoder} onValueChange={setEncoder}>
                    <SelectTrigger>
                      <SelectValue placeholder="None" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="none">None</SelectItem>
                      <SelectItem value="shikata_ga_nai">Shikata Ga Nai</SelectItem>
                      <SelectItem value="alpha_mixed">Alpha Mixed</SelectItem>
                      <SelectItem value="xor">XOR</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>

              {encoder && encoder !== "none" && (
                <div>
                  <Label htmlFor="iterations">Encoding Iterations</Label>
                  <Input
                    id="iterations"
                    type="number"
                    value={iterations}
                    onChange={(e) => setIterations(e.target.value)}
                    min="1"
                    max="10"
                  />
                </div>
              )}
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle>Advanced Options</CardTitle>
              <CardDescription>Configure evasion and persistence features</CardDescription>
            </CardHeader>
            <CardContent className="space-y-6">
              <div className="flex items-center justify-between">
                <div className="space-y-0.5">
                  <Label>Code Obfuscation</Label>
                  <p className="text-sm text-muted-foreground">
                    Apply code obfuscation techniques
                  </p>
                </div>
                <Switch checked={obfuscation} onCheckedChange={setObfuscation} />
              </div>

              <Separator />

              <div className="flex items-center justify-between">
                <div className="space-y-0.5">
                  <Label>Persistence Mechanism</Label>
                  <p className="text-sm text-muted-foreground">
                    Add persistence to maintain access
                  </p>
                </div>
                <Switch checked={persistence} onCheckedChange={setPersistence} />
              </div>

              <Separator />

              <div className="flex items-center justify-between">
                <div className="space-y-0.5">
                  <Label>Anti-Virus Evasion</Label>
                  <p className="text-sm text-muted-foreground">
                    Apply AV evasion techniques
                  </p>
                </div>
                <Switch checked={antiVirus} onCheckedChange={setAntiVirus} />
              </div>

              <Separator />

              <div>
                <Label htmlFor="custom">Custom Code</Label>
                <Textarea
                  id="custom"
                  value={customCode}
                  onChange={(e) => setCustomCode(e.target.value)}
                  placeholder="Add custom code or modifications..."
                  rows={4}
                  className="font-mono text-sm"
                />
              </div>
            </CardContent>
          </Card>
        </div>

        <div className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle>Generation Preview</CardTitle>
              <CardDescription>Review payload configuration</CardDescription>
            </CardHeader>
            <CardContent>
              {platform && payloadType ? (
                <div className="space-y-4">
                  <div className="p-4 bg-muted/50 rounded-lg">
                    <h3 className="font-medium mb-2">Configuration Summary</h3>
                    <div className="space-y-2 text-sm">
                      <div className="flex justify-between">
                        <span className="text-muted-foreground">Platform:</span>
                        <span className={selectedPlatform?.color}>{selectedPlatform?.name}</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-muted-foreground">Type:</span>
                        <span className="font-mono">{payloadType}</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-muted-foreground">Name:</span>
                        <span className="font-mono">{payloadName || "Unnamed"}</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-muted-foreground">Target:</span>
                        <span className="font-mono">{lhost}:{lport}</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-muted-foreground">Arch:</span>
                        <span className="font-mono">{architecture}</span>
                      </div>
                    </div>
                  </div>

                  <div className="space-y-2">
                    <h4 className="font-medium text-sm">Features:</h4>
                    <div className="flex flex-wrap gap-1">
                      {encoder && encoder !== "none" && <Badge variant="secondary">Encoded</Badge>}
                      {obfuscation && <Badge variant="secondary">Obfuscated</Badge>}
                      {persistence && <Badge variant="secondary">Persistent</Badge>}
                      {antiVirus && <Badge variant="secondary">AV Evasion</Badge>}
                    </div>
                  </div>
                </div>
              ) : (
                <div className="text-center py-8 text-muted-foreground">
                  <Settings className="h-8 w-8 mx-auto mb-2" />
                  <p>Configure payload to see preview</p>
                </div>
              )}
            </CardContent>
          </Card>

          <div className="space-y-3">
            <Button 
              className="w-full bg-green-600 hover:bg-green-700"
              onClick={handleGenerate}
              disabled={!platform || !payloadType || !payloadName || generateMutation.isPending}
              data-testid="button-generate-payload"
            >
              <Wand2 className="h-4 w-4 mr-2" />
              {generateMutation.isPending ? "Generating..." : "Generate Payload"}
            </Button>

            <Button 
              variant="outline" 
              className="w-full" 
              disabled={!generatedPayload || downloadMutation.isPending}
              onClick={handleDownload}
              data-testid="button-download-payload"
            >
              <Download className="h-4 w-4 mr-2" />
              {downloadMutation.isPending ? "Downloading..." : "Download Payload"}
            </Button>
          </div>

          <Card>
            <CardHeader>
              <CardTitle className="text-sm">Security Notice</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="flex items-start gap-2">
                <Shield className="h-4 w-4 text-yellow-400 mt-0.5" />
                <p className="text-xs text-muted-foreground">
                  Generated payloads are for authorized testing only. 
                  Ensure you have proper authorization before deployment.
                </p>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
      </div>
    </>
  );
}

client/src/pages/payloads/WebPayloadsPag.content:
import { useState } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import { Search, Filter, Plus, Download, Globe, Code, FileText, Info, Cog } from "lucide-react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useLocation } from "wouter";

interface WebPayload {
  id: string;
  name: string;
  type: string;
  description?: string;
  size?: number;
  config: any;
  isGenerated: boolean;
  createdAt: string;
}

function PayloadCard({ payload }: { payload: WebPayload }) {
  const [detailsOpen, setDetailsOpen] = useState(false);
  const [configOpen, setConfigOpen] = useState(false);
  const { toast } = useToast();

  const getTypeIcon = (type: string) => {
    switch (type) {
      case "webshell": return Globe;
      case "script": return Code;
      case "inject": return FileText;
      default: return Globe;
    }
  };

  const formatSize = (bytes?: number) => {
    if (!bytes) return "Unknown";
    if (bytes < 1024) return `${bytes} B`;
    if (bytes < 1024 * 1024) return `${(bytes / 1024).toFixed(1)} KB`;
    return `${(bytes / (1024 * 1024)).toFixed(1)} MB`;
  };

  const downloadMutation = useMutation({
    mutationFn: async () => {
      const response = await fetch(`/api/payloads/${payload.id}/download`, {
        method: "POST"
      });
      if (!response.ok) throw new Error("Download failed");
      return response;
    },
    onSuccess: () => {
      // Simulate file download
      const link = document.createElement('a');
      link.href = `/api/payloads/${payload.id}/download`;
      link.download = `${payload.name}.${payload.type === 'webshell' ? 'php' : 'js'}`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      toast({
        title: "Download Started",
        description: `${payload.name} download initiated successfully.`
      });
    },
    onError: () => {
      toast({
        title: "Download Failed",
        description: "Failed to download payload. Please try again.",
        variant: "destructive"
      });
    }
  });

  const handleDownload = () => {
    downloadMutation.mutate();
  };

  const handleConfigure = () => {
    setConfigOpen(true);
  };

  const handleDetails = () => {
    setDetailsOpen(true);
  };

  const Icon = getTypeIcon(payload.type);

  return (
    <Card className="bg-card/50 border-border/50 hover:bg-card/80 transition-colors">
      <CardHeader className="pb-3">
        <div className="flex items-start justify-between">
          <div className="flex items-center gap-2">
            <Icon className="h-4 w-4 text-purple-400" />
            <CardTitle className="text-sm">{payload.name}</CardTitle>
          </div>
          <div className="flex items-center gap-2">
            <Badge variant={payload.isGenerated ? "default" : "secondary"}>
              {payload.type}
            </Badge>
          </div>
        </div>
        <CardDescription className="line-clamp-2">
          {payload.description || "Web-based payload"}
        </CardDescription>
      </CardHeader>
      <CardContent className="pt-0">
        <div className="space-y-2 text-xs text-muted-foreground">
          <div>Size: {formatSize(payload.size)}</div>
          <div>Language: {payload.config?.language || "JavaScript"}</div>
          <div>Created: {new Date(payload.createdAt).toLocaleDateString()}</div>
        </div>
        <div className="flex gap-2 mt-4">
          <Button 
            size="sm" 
            className="h-7 text-xs bg-purple-600 hover:bg-purple-700"
            onClick={handleDownload}
            disabled={downloadMutation.isPending}
            data-testid={`button-download-${payload.id}`}
          >
            <Download className="h-3 w-3 mr-1" />
            {downloadMutation.isPending ? 'Downloading...' : 'Download'}
          </Button>
          <Dialog open={configOpen} onOpenChange={setConfigOpen}>
            <DialogTrigger asChild>
              <Button size="sm" variant="secondary" className="h-7 text-xs" data-testid={`button-configure-${payload.id}`}>
                <Cog className="h-3 w-3 mr-1" />
                Configure
              </Button>
            </DialogTrigger>
            <DialogContent className="sm:max-w-[425px]">
              <DialogHeader>
