server/storage.ts.content:
import { type User, type InsertUser } from "@shared/schema";
import { randomUUID } from "crypto";

// modify the interface with any CRUD methods
// you might need

export interface IStorage {
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;

  constructor() {
    this.users = new Map();
  }

  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username === username,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { ...insertUser, id };
    this.users.set(id, user);
    return user;
  }
}

export const storage = new MemStorage();

server/vite.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, integer, timestamp, boolean, json } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

// Existing users table
export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  username: text("username").notNull().unique(),
  password: text("password").notNull(),
});

export const insertUserSchema = createInsertSchema(users).pick({
  username: true,
  password: true,
});

export type InsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;

// Comprehensive client data schema for C2 management
export const clients = pgTable("clients", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  hostname: text("hostname").notNull(),
  ipAddress: text("ip_address").notNull(),
  macAddress: text("mac_address"),

  // Operating System & Hardware
  operatingSystem: text("operating_system").notNull(),
  osVersion: text("os_version"),
  architecture: text("architecture"),
  processorType: text("processor_type"),
  totalMemory: integer("total_memory"), // in GB

  // Geographic & Network Location
  country: text("country"),
  city: text("city"),
  region: text("region"),
  latitude: text("latitude"),
  longitude: text("longitude"),
  timezone: text("timezone"),

  // Connection & Status
  status: text("status").notNull(), // online, offline, compromised, error
  lastSeen: timestamp("last_seen", { withTimezone: true }).defaultNow(),
  firstSeen: timestamp("first_seen", { withTimezone: true }).defaultNow(),
  uptime: integer("uptime"), // in seconds
  connectionQuality: text("connection_quality"), // excellent, good, fair, poor

  // Security & Privileges
  currentUser: text("current_user"),
  isElevated: boolean("is_elevated").default(false),
  availablePrivileges: json("available_privileges"), // array of privilege types
  userAccounts: json("user_accounts"), // array of user account objects

  // System Information
  installedSoftware: json("installed_software"), // array of software objects
  runningProcesses: json("running_processes"), // array of process objects
  openPorts: json("open_ports"), // array of port objects
  networkInterfaces: json("network_interfaces"), // array of network interface objects

  // Assessment & Classification
  riskLevel: text("risk_level"), // low, medium, high, critical
  vulnerabilityCount: integer("vulnerability_count").default(0),
  difficulty: text("difficulty"), // beginner, intermediate, advanced

  // Metadata
  tags: json("tags"), // array of strings
  notes: text("notes"),
  isActive: boolean("is_active").default(true),

  createdAt: timestamp("created_at", { withTimezone: true }).defaultNow(),
  updatedAt: timestamp("updated_at", { withTimezone: true }).defaultNow(),
});

export const insertClientSchema = createInsertSchema(clients).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export type InsertClient = z.infer<typeof insertClientSchema>;
export type Client = typeof clients.$inferSelect;

// Session management schema
export const sessions = pgTable("sessions", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  clientId: varchar("client_id").notNull(),
  sessionType: text("session_type").notNull(), // shell, file_manager, remote_desktop, surveillance
  status: text("status").notNull(), // active, inactive, terminated

  startedAt: timestamp("started_at", { withTimezone: true }).defaultNow(),
  lastActivity: timestamp("last_activity", { withTimezone: true }).defaultNow(),
  endedAt: timestamp("ended_at", { withTimezone: true }),

  metadata: json("metadata"), // session-specific data
});

export const insertSessionSchema = createInsertSchema(sessions).omit({
  id: true,
  startedAt: true,
  lastActivity: true,
});

export type InsertSession = z.infer<typeof insertSessionSchema>;
export type Session = typeof sessions.$inferSelect;

// Enhanced type definitions for frontend use
export interface ClientSystemInfo {
  os: string;
  version: string;
  architecture: string;
  processor: string;
  memory: number;
  uptime: number;
}

export interface ClientLocation {
  country: string;
  city: string;
  region: string;
  latitude: number;
  longitude: number;
  timezone: string;
}

export interface ClientUserAccount {
  username: string;
  isAdmin: boolean;
  isActive: boolean;
  lastLogin?: Date;
  groups: string[];
}

export interface ClientSoftware {
  name: string;
  version: string;
  vendor: string;
  installDate?: Date;
  isSystemCritical: boolean;
}

export interface ClientProcess {
  pid: number;
  name: string;
  cpu: number;
  memory: number;
  user: string;
  startTime: Date;
}

export interface ClientNetworkInterface {
  name: string;
  type: string; // ethernet, wifi, vpn
  ipAddress: string;
  macAddress: string;
  isActive: boolean;
  speed?: number;
}

export interface ClientPort {
  port: number;
  protocol: string; // TCP, UDP
  state: string; // LISTENING, ESTABLISHED
  service?: string;
  processName?: string;
}

client/src/App.tsx.content:
import { Switch, Route } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { TopNavbar } from "@/components/TopNavbar";
import Dashboard from "@/pages/Dashboard";
import ClientsPage from "@/pages/ClientsPage";
import TerminalPage from "@/pages/TerminalPage";
import NetworkPage from "@/pages/NetworkPage";
import ScenariosPage from "@/pages/ScenariosPage";
import TelemetryPage from "@/pages/TelemetryPage";
import NotFound from "@/pages/not-found";
import { useEffect } from "react";

function Router() {
  return (
    <Switch>
      <Route path="/" component={Dashboard} />
      <Route path="/clients" component={ClientsPage} />
      <Route path="/remote-access" component={TerminalPage} />
      <Route path="/network" component={NetworkPage} />
      <Route path="/scenarios" component={ScenariosPage} />
      <Route path="/surveillance" component={() => <div className="container-responsive py-6"><div className="text-center py-20"><h1 className="text-2xl font-semibold mb-4">Surveillance & Monitoring</h1><p className="text-muted-foreground">Advanced surveillance features coming soon...</p></div></div>} />
      <Route path="/post-exploitation" component={() => <div className="container-responsive py-6"><div className="text-center py-20"><h1 className="text-2xl font-semibold mb-4">Post-Exploitation Tools</h1><p className="text-muted-foreground">Advanced exploitation tools coming soon...</p></div></div>} />
      <Route path="/automation" component={() => <div className="container-responsive py-6"><div className="text-center py-20"><h1 className="text-2xl font-semibold mb-4">Automation & Tasking</h1><p className="text-muted-foreground">Command automation features coming soon...</p></div></div>} />
      <Route path="/telemetry" component={TelemetryPage} />
      <Route path="/batch" component={() => <div className="container-responsive py-6"><div className="text-center py-20"><h1 className="text-2xl font-semibold mb-4">Batch Operations</h1><p className="text-muted-foreground">Advanced batch command execution coming soon...</p></div></div>} />
      <Route path="/analysis" component={() => <div className="container-responsive py-6"><div className="text-center py-20"><h1 className="text-2xl font-semibold mb-4">Analysis & Intelligence</h1><p className="text-muted-foreground">Data analysis and intelligence tools coming soon...</p></div></div>} />
      <Route path="/reports" component={() => <div className="container-responsive py-6"><div className="text-center py-20"><h1 className="text-2xl font-semibold mb-4">Reports & Documentation</h1><p className="text-muted-foreground">Operational reports and documentation coming soon...</p></div></div>} />
      <Route path="/users" component={() => <div className="container-responsive py-6"><div className="text-center py-20"><h1 className="text-2xl font-semibold mb-4">User Management</h1><p className="text-muted-foreground">User access control and management coming soon...</p></div></div>} />
      <Route path="/logs" component={() => <div className="container-responsive py-6"><div className="text-center py-20"><h1 className="text-2xl font-semibold mb-4">System Logs</h1><p className="text-muted-foreground">Comprehensive system logging and audit trails coming soon...</p></div></div>} />
      <Route path="/resources" component={() => <div className="container-responsive py-6"><div className="text-center py-20"><h1 className="text-2xl font-semibold mb-4">Resources</h1><p className="text-muted-foreground">Resources page coming soon...</p></div></div>} />
      <Route path="/settings" component={() => <div className="container-responsive py-6"><div className="text-center py-20"><h1 className="text-2xl font-semibold mb-4">Settings</h1><p className="text-muted-foreground">Settings page coming soon...</p></div></div>} />
      <Route component={NotFound} />
    </Switch>
  );
}

function App() {
  // Initialize dark mode
  useEffect(() => {
    document.documentElement.classList.add('dark');
  }, []);

  return (
    <QueryClientProvider client={queryClient}>
      <TooltipProvider>
        <div className="min-h-screen w-full bg-background text-foreground">
          <TopNavbar />
          <main className="w-full">
            <Router />
          </main>
        </div>
        <Toaster />
      </TooltipProvider>
    </QueryClientProvider>
  );
}

export default App;

