}

export function LabEnvironmentSelector() {
  //todo: remove mock functionality
  const environments: LabEnvironment[] = [
    {
      id: 'windows-domain',
      name: 'Windows Domain Compromise',
      description: 'Learn lateral movement and persistence techniques in an Active Directory environment.',
      difficulty: 'intermediate',
      duration: '45-60 min',
      participants: 1,
      maxParticipants: 4,
      techniques: ['Lateral Movement', 'Credential Dumping', 'Domain Admin'],
      status: 'available'
    },
    {
      id: 'linux-privilege-esc',
      name: 'Linux Privilege Escalation',
      description: 'Explore various methods to escalate privileges from a low-privileged user account.',
      difficulty: 'beginner',
      duration: '30-45 min',
      participants: 2,
      maxParticipants: 3,
      techniques: ['SUID Binaries', 'Cron Jobs', 'Sudo Exploitation'],
      status: 'in-progress'
    },
    {
      id: 'web-app-pentest',
      name: 'Web Application Penetration',
      description: 'Advanced techniques for compromising web applications and maintaining access.',
      difficulty: 'advanced',
      duration: '60-90 min',
      participants: 0,
      maxParticipants: 2,
      techniques: ['SQL Injection', 'XSS', 'Command Injection', 'File Upload'],
      status: 'completed'
    },
    {
      id: 'network-pivoting',
      name: 'Network Pivoting & Tunneling',
      description: 'Master network pivoting techniques to access isolated network segments.',
      difficulty: 'advanced',
      duration: '75-90 min',
      participants: 0,
      maxParticipants: 3,
      techniques: ['Proxychains', 'SSH Tunneling', 'Meterpreter Pivoting'],
      status: 'available'
    }
  ];

  const getDifficultyColor = (difficulty: string) => {
    switch (difficulty) {
      case 'beginner': return 'bg-success text-success-foreground';
      case 'intermediate': return 'bg-warning text-warning-foreground';
      case 'advanced': return 'bg-destructive text-destructive-foreground';
      default: return 'bg-secondary text-secondary-foreground';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'available': return <Play className="w-4 h-4" />;
      case 'in-progress': return <Clock className="w-4 h-4 text-warning" />;
      case 'completed': return <CheckCircle className="w-4 h-4 text-success" />;
      default: return <AlertTriangle className="w-4 h-4" />;
    }
  };

  const handleStartLab = (envId: string) => {
    console.log(`Starting lab environment: ${envId}`);
  };

  return (
    <div className="space-y-4" data-testid="lab-environment-selector">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-lg font-semibold">Lab Environments</h2>
          <p className="text-sm text-muted-foreground">Choose a scenario to practice post-exploitation techniques</p>
        </div>
        <Badge variant="outline" className="text-xs">
          {environments.filter(e => e.status === 'available').length} Available
        </Badge>
      </div>

      <div className="grid gap-4">
        {environments.map((env) => (
          <Card key={env.id} className="hover-elevate" data-testid={`lab-environment-${env.id}`}>
            <CardHeader className="pb-3">
              <div className="flex items-start justify-between">
                <div className="space-y-1">
                  <div className="flex items-center gap-2">
                    {getStatusIcon(env.status)}
                    <CardTitle className="text-base">{env.name}</CardTitle>
                  </div>
                  <CardDescription className="text-sm">{env.description}</CardDescription>
                </div>
                <Badge className={`${getDifficultyColor(env.difficulty)} text-xs`}>
                  {env.difficulty}
                </Badge>
              </div>
            </CardHeader>

            <CardContent className="space-y-4">
              <div className="flex items-center gap-4 text-xs text-muted-foreground">
                <div className="flex items-center gap-1">
                  <Clock className="w-3 h-3" />
                  <span>{env.duration}</span>
                </div>
                <div className="flex items-center gap-1">
                  <Users className="w-3 h-3" />
                  <span>{env.participants}/{env.maxParticipants} participants</span>
                </div>
              </div>

              <div className="flex flex-wrap gap-1">
                {env.techniques.map((technique, index) => (
                  <Badge key={index} variant="secondary" className="text-xs">
                    {technique}
                  </Badge>
                ))}
              </div>

              <div className="flex justify-between items-center">
                <div className="flex items-center gap-2">
                  <Shield className="w-4 h-4 text-muted-foreground" />
                  <span className="text-xs text-muted-foreground">Isolated Environment</span>
                </div>
                <Button 
                  size="sm"
                  variant={env.status === 'available' ? 'default' : 'secondary'}
                  disabled={env.status === 'in-progress'}
                  onClick={() => handleStartLab(env.id)}
                  data-testid={`button-start-${env.id}`}
                >
                  {env.status === 'available' ? 'Start Lab' : 
                   env.status === 'in-progress' ? 'In Progress' : 'Review'}
                </Button>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
}

client/src/components/NetworkTopology.ts.content:
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Network, Router, Monitor, Server, Smartphone, Shield } from "lucide-react";
import { useState } from "react";

interface NetworkNode {
  id: string;
  name: string;
  type: 'router' | 'server' | 'workstation' | 'mobile' | 'firewall';
  status: 'online' | 'offline' | 'compromised';
  ip: string;
  x: number;
  y: number;
}

export function NetworkTopology() {
  //todo: remove mock functionality
  const [nodes] = useState<NetworkNode[]>([
    { id: 'router', name: 'Gateway Router', type: 'router', status: 'online', ip: '192.168.1.1', x: 50, y: 20 },
    { id: 'firewall', name: 'Firewall', type: 'firewall', status: 'online', ip: '192.168.1.2', x: 50, y: 45 },
    { id: 'server1', name: 'Web Server', type: 'server', status: 'compromised', ip: '192.168.1.10', x: 20, y: 70 },
    { id: 'server2', name: 'DB Server', type: 'server', status: 'online', ip: '192.168.1.11', x: 50, y: 70 },
    { id: 'workstation1', name: 'Admin PC', type: 'workstation', status: 'online', ip: '192.168.1.20', x: 80, y: 70 },
    { id: 'workstation2', name: 'User PC', type: 'workstation', status: 'offline', ip: '192.168.1.21', x: 20, y: 90 },
    { id: 'mobile1', name: 'Mobile Device', type: 'mobile', status: 'online', ip: '192.168.1.30', x: 80, y: 90 },
  ]);

  const [selectedNode, setSelectedNode] = useState<string | null>(null);

  const getIcon = (type: NetworkNode['type']) => {
    switch (type) {
      case 'router': return Router;
      case 'server': return Server;
      case 'workstation': return Monitor;
      case 'mobile': return Smartphone;
      case 'firewall': return Shield;
      default: return Monitor;
    }
  };

  const getStatusColor = (status: NetworkNode['status']) => {
    switch (status) {
      case 'online': return 'text-success';
      case 'offline': return 'text-muted-foreground';
      case 'compromised': return 'text-warning';
      default: return 'text-muted-foreground';
    }
  };

  const connections = [
    { from: 'router', to: 'firewall' },
    { from: 'firewall', to: 'server1' },
    { from: 'firewall', to: 'server2' },
    { from: 'firewall', to: 'workstation1' },
    { from: 'firewall', to: 'workstation2' },
    { from: 'firewall', to: 'mobile1' },
  ];

  const handleNodeClick = (nodeId: string) => {
    setSelectedNode(nodeId);
    console.log('Selected network node:', nodeId);
  };

  return (
    <Card data-testid="network-topology">
      <CardHeader>
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-2">
            <Network className="w-5 h-5 text-primary" />
            <div>
              <CardTitle>Network Topology</CardTitle>
              <CardDescription>Lab network layout and device status</CardDescription>
            </div>
          </div>
          <Badge variant="outline" className="text-xs">
            7 devices
          </Badge>
        </div>
      </CardHeader>

      <CardContent className="p-6">
        <div className="relative bg-muted/20 rounded-lg p-4 min-h-[400px]">
          {/* Connection lines */}
          <svg className="absolute inset-0 w-full h-full pointer-events-none">
            {connections.map((connection, index) => {
              const fromNode = nodes.find(n => n.id === connection.from);
              const toNode = nodes.find(n => n.id === connection.to);
              if (!fromNode || !toNode) return null;

              return (
                <line
                  key={index}
                  x1={`${fromNode.x}%`}
                  y1={`${fromNode.y}%`}
                  x2={`${toNode.x}%`}
                  y2={`${toNode.y}%`}
                  stroke="hsl(var(--border))"
                  strokeWidth="1"
                  strokeDasharray="2,2"
                />
              );
            })}
          </svg>

          {/* Network nodes */}
          {nodes.map((node) => {
            const IconComponent = getIcon(node.type);
            const isSelected = selectedNode === node.id;

            return (
              <div
                key={node.id}
                className="absolute transform -translate-x-1/2 -translate-y-1/2 cursor-pointer"
                style={{ left: `${node.x}%`, top: `${node.y}%` }}
                onClick={() => handleNodeClick(node.id)}
                data-testid={`node-${node.id}`}
              >
                <div className={`
                  flex flex-col items-center gap-1 p-2 rounded-lg border
                  ${isSelected ? 'bg-primary/20 border-primary' : 'bg-card border-border'}
                  hover-elevate
                `}>
                  <IconComponent className={`w-6 h-6 ${getStatusColor(node.status)}`} />
                  <div className="text-center">
                    <div className="text-xs font-medium">{node.name}</div>
                    <div className="text-xs text-muted-foreground font-mono">{node.ip}</div>
                    <Badge 
                      variant={node.status === 'compromised' ? 'destructive' : 'secondary'} 
                      className="text-xs mt-1"
                    >
                      {node.status}
                    </Badge>
                  </div>
                </div>
              </div>
            );
          })}
        </div>

        {selectedNode && (
          <div className="mt-4 p-3 bg-muted rounded-lg">
            <div className="flex items-center justify-between">
              <div>
                <h4 className="text-sm font-medium">
                  {nodes.find(n => n.id === selectedNode)?.name}
                </h4>
                <p className="text-xs text-muted-foreground font-mono">
                  {nodes.find(n => n.id === selectedNode)?.ip}
                </p>
              </div>
              <Button size="sm" variant="outline" data-testid={`button-scan-${selectedNode}`}>
                Scan Device
              </Button>
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/TargetMachineCard..content:
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { 
  Monitor, Wifi, WifiOff, Shield, AlertTriangle, CheckCircle, 
  Server, Globe, Lock, Users, HardDrive, Activity, Eye, Terminal
} from "lucide-react";

interface TargetMachineCardProps {
  id: string;
  name: string;
  os: string;
  ip: string;
  status: 'online' | 'offline' | 'compromised';
  vulnerabilities: number;
  difficulty: 'beginner' | 'intermediate' | 'advanced';
  domain?: string;
  services?: string[];
}

export function TargetMachineCard({ 
  id, name, os, ip, status, vulnerabilities, difficulty, domain = "N/A", services = []
}: TargetMachineCardProps) {
  const statusConfig = {
    online: { 
      color: 'text-green-600 dark:text-green-400', 
      icon: Wifi, 
      text: 'Online',
      bgColor: 'bg-green-50 dark:bg-green-950',
      borderColor: 'border-green-200 dark:border-green-800'
    },
    offline: { 
      color: 'text-gray-500 dark:text-gray-400', 
      icon: WifiOff, 
      text: 'Offline',
      bgColor: 'bg-gray-50 dark:bg-gray-900',
      borderColor: 'border-gray-200 dark:border-gray-700'
    },
    compromised: { 
      color: 'text-amber-600 dark:text-amber-400', 
      icon: AlertTriangle, 
      text: 'Compromised',
      bgColor: 'bg-amber-50 dark:bg-amber-950',
      borderColor: 'border-amber-200 dark:border-amber-800'
    }
  };

  const difficultyConfig = {
    beginner: { 
      color: 'text-green-700 dark:text-green-300', 
      text: 'Beginner',
      bgColor: 'bg-green-100 dark:bg-green-900'
    },
    intermediate: { 
      color: 'text-amber-700 dark:text-amber-300', 
      text: 'Intermediate',
      bgColor: 'bg-amber-100 dark:bg-amber-900'
    },
    advanced: { 
      color: 'text-red-700 dark:text-red-300', 
      text: 'Advanced',
      bgColor: 'bg-red-100 dark:bg-red-900'
    }
  };

  const getRiskLevel = () => {
    if (status === 'compromised') return 'HIGH';
    if (vulnerabilities > 10) return 'CRITICAL';
    if (vulnerabilities > 5) return 'HIGH';
    if (vulnerabilities > 2) return 'MEDIUM';
    return 'LOW';
  };

  const handleConnect = () => {
    console.log(`Connecting to target machine: ${name} (${ip})`);
  };

  const handleViewDetails = () => {
    console.log(`Viewing details for target machine: ${name}`);
  };

  const StatusIcon = statusConfig[status].icon;
  const riskLevel = getRiskLevel();

  return (
    <Card 
      className={`gov-card transition-all duration-200 ${statusConfig[status].borderColor} hover:shadow-lg`} 
      data-testid={`card-target-${id}`}
    >
      <CardHeader className="pb-4">
        <div className="flex items-start justify-between">
          <div className="flex items-center gap-3">
            <div className={`p-2 rounded-lg ${statusConfig[status].bgColor}`}>
              <Server className={`w-5 h-5 ${statusConfig[status].color}`} />
            </div>
            <div className="space-y-1">
              <CardTitle className="text-base font-semibold gov-header">{name}</CardTitle>
              <div className="flex items-center gap-2 text-sm text-muted-foreground">
                <Globe className="w-3 h-3" />
                <span className="font-mono">{ip}</span>
              </div>
            </div>
          </div>
          <div className="flex items-center gap-2">
            <div className={`w-2 h-2 rounded-full ${
              status === 'online' ? 'status-online' : 
              status === 'compromised' ? 'status-compromised' : 
              'status-offline'
            }`}></div>
            <StatusIcon className={`w-4 h-4 ${statusConfig[status].color}`} />
          </div>
        </div>
      </CardHeader>

      <CardContent className="space-y-4">
        {/* System Information */}
        <div className="grid grid-cols-2 gap-3 text-xs">
          <div className="space-y-1">
            <span className="text-muted-foreground uppercase tracking-wide">OS</span>
            <p className="font-medium gov-body">{os}</p>
          </div>
          <div className="space-y-1">
            <span className="text-muted-foreground uppercase tracking-wide">Domain</span>
            <p className="font-medium font-mono">{domain}</p>
          </div>
        </div>

        {/* Risk Assessment */}
        <div className="flex items-center justify-between p-3 rounded-lg bg-muted/30 border border-border">
          <div className="space-y-1">
            <span className="text-xs text-muted-foreground uppercase tracking-wide">Risk Level</span>
            <div className="flex items-center gap-2">
              <Badge 
                variant={
                  riskLevel === 'CRITICAL' ? 'destructive' : 
                  riskLevel === 'HIGH' ? 'default' : 
                  riskLevel === 'MEDIUM' ? 'secondary' : 
                  'outline'
                }
                className="text-xs font-mono"
              >
                {riskLevel}
              </Badge>
              <span className="text-sm text-muted-foreground">
                {vulnerabilities} CVE{vulnerabilities !== 1 ? 's' : ''}
              </span>
            </div>
          </div>
          <div className="text-right">
            <Badge 
              className={`${difficultyConfig[difficulty].bgColor} ${difficultyConfig[difficulty].color} border-0 text-xs`}
            >
              {difficultyConfig[difficulty].text}
            </Badge>
          </div>
        </div>

        {/* Services */}
        {services.length > 0 && (
          <div className="space-y-2">
            <span className="text-xs text-muted-foreground uppercase tracking-wide">Services</span>
            <div className="flex flex-wrap gap-1">
              {services.slice(0, 4).map((service, index) => (
                <Badge key={index} variant="outline" className="text-xs font-mono px-2 py-1">
                  {service}
                </Badge>
              ))}
              {services.length > 4 && (
                <Badge variant="outline" className="text-xs px-2 py-1">
                  +{services.length - 4} more
                </Badge>
              )}
            </div>
          </div>
        )}

        {/* Action Buttons */}
        <div className="flex items-center gap-2 pt-2">
          <Button 
            size="sm" 
            variant={status === 'compromised' ? 'default' : 'outline'}
            className="flex-1" 
            onClick={handleConnect}
            disabled={status === 'offline'}
            data-testid={`button-connect-${id}`}
          >
            {status === 'compromised' ? (
              <>
                <Terminal className="w-3 h-3 mr-1" />
                Access Shell
              </>
            ) : status === 'online' ? (
              <>
                <Lock className="w-3 h-3 mr-1" />
                Exploit
              </>
            ) : (
              <>
                <WifiOff className="w-3 h-3 mr-1" />
                Offline
              </>
            )}
          </Button>
          <Button 
            size="sm" 
            variant="ghost"
            onClick={handleViewDetails}
            data-testid={`button-details-${id}`}
          >
            <Eye className="w-3 h-3" />
          </Button>
        </div>

        {/* Status Footer */}
        <div className="flex items-center justify-between text-xs text-muted-foreground pt-2 border-t border-border">
          <span className="flex items-center gap-1">
            <Activity className="w-3 h-3" />
            {statusConfig[status].text}
          </span>
          <span className="font-mono">
            Last seen: {status === 'offline' ? '2h ago' : 'Now'}
          </span>
        </div>
      </CardContent>
    </Card>
  );
}

