import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Badge } from "@/components/ui/badge";
import { 
  Terminal, X, Minimize2, Maximize2, Wifi, Shield, Lock, 
  Activity, Clock, User, HardDrive, Cpu, Globe 
} from "lucide-react";
import { useState, useRef, useEffect } from "react";

interface TerminalLog {
  id: number;
  timestamp: string;
  command?: string;
  output?: string;
  type: 'command' | 'output' | 'error' | 'success';
}

export function TerminalInterface() {
  const [logs, setLogs] = useState<TerminalLog[]>([
    { id: 1, timestamp: '14:32:01', output: '=== BYOB-LAB SECURE TERMINAL v3.2.1 ===', type: 'success' },
    { id: 2, timestamp: '14:32:01', output: 'CLASSIFICATION: SIMULATION ENVIRONMENT', type: 'output' },
    { id: 3, timestamp: '14:32:01', output: 'ENCRYPTION: AES-256 | SESSION: ESTABLISHED', type: 'output' },
    { id: 4, timestamp: '14:32:02', output: 'TARGET: WIN-SERVER2019 (192.168.1.10)', type: 'success' },
    { id: 5, timestamp: '14:32:02', output: 'ACCESS LEVEL: SYSTEM | PRIVILEGES: ELEVATED', type: 'success' },
    { id: 6, timestamp: '14:32:03', output: 'Ready for operations...', type: 'output' },
  ]);
  const [currentCommand, setCurrentCommand] = useState('');
  const [isMaximized, setIsMaximized] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState('SECURE');
  const [sessionTime, setSessionTime] = useState('00:15:42');
  const scrollRef = useRef<HTMLDivElement>(null);

  // Simulation commands for educational purposes
  const mockCommands: { [key: string]: { output: string; type: 'output' | 'error' | 'success' } } = {
    'help': { output: '[SYSTEM] Available commands:\n‚îú‚îÄ‚îÄ whoami          - Display current user context\n‚îú‚îÄ‚îÄ sysinfo         - System information\n‚îú‚îÄ‚îÄ netstat         - Network connections\n‚îú‚îÄ‚îÄ dir / ls        - Directory listing\n‚îú‚îÄ‚îÄ ps              - Process listing\n‚îú‚îÄ‚îÄ ipconfig        - Network configuration\n‚îú‚îÄ‚îÄ cat <file>      - Display file contents\n‚îî‚îÄ‚îÄ exit            - Terminate session', type: 'output' },
    'sysinfo': { output: '[TARGET] System Information:\n‚îú‚îÄ‚îÄ Hostname: WIN-SERVER2019\n‚îú‚îÄ‚îÄ OS: Windows Server 2019 Standard\n‚îú‚îÄ‚îÄ Architecture: x64\n‚îú‚îÄ‚îÄ Domain: CORPORATE.local\n‚îú‚îÄ‚îÄ Last Boot: 2024-03-14 08:30:15\n‚îî‚îÄ‚îÄ Uptime: 6 days, 14 hours', type: 'output' },
    'whoami': { output: 'NT AUTHORITY\\SYSTEM', type: 'success' },
    'ps': { output: '[PROCESSES] Active processes:\n‚îú‚îÄ‚îÄ PID 1234 - explorer.exe (Administrator)\n‚îú‚îÄ‚îÄ PID 2156 - svchost.exe (SYSTEM)\n‚îú‚îÄ‚îÄ PID 3421 - winlogon.exe (SYSTEM)\n‚îî‚îÄ‚îÄ PID 4567 - services.exe (SYSTEM)', type: 'output' },
    'ipconfig': { output: '[NETWORK] Interface Configuration:\n‚îú‚îÄ‚îÄ Adapter: Ethernet0\n‚îú‚îÄ‚îÄ IPv4: 192.168.1.10/24\n‚îú‚îÄ‚îÄ Gateway: 192.168.1.1\n‚îú‚îÄ‚îÄ DNS1: 8.8.8.8\n‚îî‚îÄ‚îÄ Status: Connected', type: 'output' },
    'netstat': { output: '[NETWORK] Active Connections:\n‚îú‚îÄ‚îÄ TCP 192.168.1.10:135 ‚Üí 0.0.0.0:0 [LISTENING]\n‚îú‚îÄ‚îÄ TCP 192.168.1.10:445 ‚Üí 0.0.0.0:0 [LISTENING]\n‚îú‚îÄ‚îÄ TCP 192.168.1.10:3389 ‚Üí 0.0.0.0:0 [LISTENING]\n‚îî‚îÄ‚îÄ TCP 192.168.1.10:5985 ‚Üí 0.0.0.0:0 [LISTENING]', type: 'output' },
    'dir': { output: '[DIRECTORY] C:\\Users\\Administrator\n‚îú‚îÄ‚îÄ üìÅ Documents\n‚îú‚îÄ‚îÄ üìÅ Desktop\n‚îú‚îÄ‚îÄ üìÅ Downloads\n‚îú‚îÄ‚îÄ üìÑ flag.txt (2.1 KB)\n‚îî‚îÄ‚îÄ üìÑ notes.txt (1.5 KB)', type: 'output' },
    'ls': { output: '[DIRECTORY] /home/admin\n‚îú‚îÄ‚îÄ üìÅ documents/\n‚îú‚îÄ‚îÄ üìÅ scripts/\n‚îú‚îÄ‚îÄ üìÑ .bash_history\n‚îî‚îÄ‚îÄ üìÑ credentials.txt', type: 'output' },
    'cat flag.txt': { output: '[FILE] flag.txt:\nBYOB{w3lc0m3_t0_th3_l4b_3nv1r0nm3nt}\n\n[SIMULATION] Educational flag captured!', type: 'success' },
    'exit': { output: '[SESSION] Connection terminated by user.\n[SECURITY] All activities logged for training analysis.', type: 'error' },
  };

  const executeCommand = () => {
    if (!currentCommand.trim()) return;

    const timestamp = new Date().toLocaleTimeString('en-US', { hour12: false });
    const commandLog: TerminalLog = {
      id: logs.length + 1,
      timestamp,
      command: currentCommand,
      type: 'command'
    };

    const response = mockCommands[currentCommand.toLowerCase()] || { 
      output: `Command '${currentCommand}' not recognized. Type 'help' for available commands.`, 
      type: 'error' as const 
    };

    const outputLog: TerminalLog = {
      id: logs.length + 2,
      timestamp,
      output: response.output,
      type: response.type
    };

    setLogs(prev => [...prev, commandLog, outputLog]);
    setCurrentCommand('');
    console.log('Executed command:', currentCommand);
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
      executeCommand();
    }
  };

  useEffect(() => {
    if (scrollRef.current) {
      scrollRef.current.scrollTop = scrollRef.current.scrollHeight;
    }
  }, [logs]);

  // Update session time every second
  useEffect(() => {
    const timer = setInterval(() => {
      const now = new Date();
      const start = new Date(now.getTime() - 942000); // 15:42 ago
      const diff = now.getTime() - start.getTime();
      const hours = Math.floor(diff / 3600000).toString().padStart(2, '0');
      const minutes = Math.floor((diff % 3600000) / 60000).toString().padStart(2, '0');
      const seconds = Math.floor((diff % 60000) / 1000).toString().padStart(2, '0');
      setSessionTime(`${hours}:${minutes}:${seconds}`);
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  return (
    <div className={`${isMaximized ? 'fixed inset-4 z-50' : 'h-full'} flex flex-col bg-black/95 border border-green-500/30 rounded-lg overflow-hidden`} data-testid="terminal-interface">
      {/* Terminal Header */}
      <div className="flex items-center justify-between bg-gray-900/90 border-b border-green-500/20 px-4 py-2">
        <div className="flex items-center gap-4">
          <div className="flex items-center gap-2">
            <Terminal className="w-4 h-4 text-green-400" />
            <span className="font-mono text-xs text-green-400 font-semibold">SECURE SHELL</span>
          </div>

          <div className="flex items-center gap-3 text-xs font-mono">
            <div className="flex items-center gap-1">
              <Globe className="w-3 h-3 text-green-400" />
              <span className="text-green-400">192.168.1.10</span>
            </div>
            <div className="w-px h-3 bg-green-500/20"></div>
            <div className="flex items-center gap-1">
              <User className="w-3 h-3 text-blue-400" />
              <span className="text-blue-400">SYSTEM</span>
            </div>
          </div>
        </div>

        <div className="flex items-center gap-3">
          <div className="flex items-center gap-4 text-xs font-mono">
            <div className="flex items-center gap-1">
              <Clock className="w-3 h-3 text-yellow-400" />
              <span className="text-yellow-400">{sessionTime}</span>
            </div>
            <Badge 
              variant="outline" 
              className={`text-xs font-mono ${
                connectionStatus === 'SECURE' ? 'border-green-500/30 text-green-400' : 'border-red-500/30 text-red-400'
              }`}
            >
              <Lock className="w-2 h-2 mr-1" />
              {connectionStatus}
            </Badge>
          </div>

          <div className="flex items-center gap-1 ml-2">
            <Button 
              variant="ghost" 
              size="icon" 
              className="h-6 w-6 text-gray-400 hover:text-white"
              data-testid="button-terminal-minimize"
            >
              <Minimize2 className="w-3 h-3" />
            </Button>
            <Button 
              variant="ghost" 
              size="icon" 
              className="h-6 w-6 text-gray-400 hover:text-white"
              onClick={() => setIsMaximized(!isMaximized)}
              data-testid="button-terminal-maximize"
            >
              <Maximize2 className="w-3 h-3" />
            </Button>
            <Button 
              variant="ghost" 
              size="icon" 
              className="h-6 w-6 text-gray-400 hover:text-red-400"
              data-testid="button-terminal-close"
            >
              <X className="w-3 h-3" />
            </Button>
          </div>
        </div>
      </div>

      {/* Terminal Body */}
      <div className="flex-1 flex flex-col">
        <ScrollArea className={`flex-1 p-4 font-mono text-sm bg-black/95`} ref={scrollRef}>
          <div className="space-y-1">
            {logs.map((log) => (
              <div key={log.id} className="flex gap-2 text-xs leading-relaxed">
                <span className="text-gray-500 min-w-[60px] select-none">[{log.timestamp}]</span>
                {log.command ? (
                  <div className="flex gap-2 items-center">
                    <span className="text-green-400 font-bold">root@target:~#</span>
                    <span className="text-white">{log.command}</span>
                  </div>
                ) : (
                  <span 
                    className={`${
                      log.type === 'error' ? 'text-red-400' : 
                      log.type === 'success' ? 'text-green-400' : 
                      'text-cyan-400'
                    } whitespace-pre-wrap leading-relaxed`}
                  >
                    {log.output}
                  </span>
                )}
              </div>
            ))}
          </div>
        </ScrollArea>

        {/* Command Input */}
        <div className="border-t border-green-500/20 bg-gray-900/50 p-3">
          <div className="flex items-center gap-3 font-mono text-sm">
            <div className="flex items-center gap-2 text-green-400 font-bold shrink-0">
              <span>root@target:~#</span>
            </div>
            <Input
              value={currentCommand}
              onChange={(e) => setCurrentCommand(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Enter command..."
              className="border-none bg-transparent focus-visible:ring-0 text-white font-mono placeholder:text-gray-500 p-0 h-auto"
              data-testid="input-terminal-command"
            />
          </div>
        </div>
      </div>

      {/* Status Bar */}
      <div className="flex items-center justify-between bg-gray-900/90 border-t border-green-500/20 px-4 py-1 text-xs font-mono">
        <div className="flex items-center gap-4 text-gray-400">
          <div className="flex items-center gap-1">
            <Activity className="w-3 h-3" />
            <span>Session Active</span>
          </div>
          <div className="flex items-center gap-1">
            <HardDrive className="w-3 h-3" />
            <span>C:\\ (78% free)</span>
          </div>
          <div className="flex items-center gap-1">
            <Cpu className="w-3 h-3" />
            <span>CPU: 23%</span>
          </div>
        </div>

        <div className="text-orange-400 text-xs">
          SIMULATION ENVIRONMENT - EDUCATIONAL USE ONLY
        </div>
      </div>
    </div>
  );
}

client/src/components/TopNavbar.tsx.content:
import { 
  Shield, Terminal, Network, Target, BookOpen, Activity, Settings, Play, 
  Sun, Moon, ChevronDown, Menu, X, Users, Lock, Zap, Database, FileText,
  BarChart3, Cpu, Globe, Search, Eye, Cog, Bot, Command
} from "lucide-react";
import { Link, useLocation } from "wouter";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuGroup,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { useState } from "react";

const navigationGroups = {
  command: {
    title: "Command Center",
    icon: BarChart3,
    items: [
      { title: "Dashboard", url: "/", icon: BarChart3 },
      { title: "Client Management", url: "/clients", icon: Bot },
      { title: "Network Map", url: "/network", icon: Network },
    ]
  },
  operations: {
    title: "Operations", 
    icon: Terminal,
    items: [
      { title: "Remote Access", url: "/remote-access", icon: Terminal },
      { title: "Surveillance", url: "/surveillance", icon: Eye },
      { title: "Post-Exploitation", url: "/post-exploitation", icon: Lock },
    ]
  },
  automation: {
    title: "Automation",
    icon: Command,
    items: [
      { title: "Task Management", url: "/automation", icon: Command },
      { title: "Script Execution", url: "/scenarios", icon: Play },
      { title: "Batch Operations", url: "/batch", icon: Users },
    ]
  },
  intelligence: {
    title: "Intelligence",
    icon: Activity,
    items: [
      { title: "Telemetry", url: "/telemetry", icon: Activity },
      { title: "Threat Analysis", url: "/analysis", icon: Search },
      { title: "Reports", url: "/reports", icon: FileText },
    ]
  },
  system: {
    title: "System",
    icon: Settings,
    items: [
      { title: "Configuration", url: "/settings", icon: Settings },
      { title: "User Management", url: "/users", icon: Users },
      { title: "Security Logs", url: "/logs", icon: Database },
    ]
  }
};

export function TopNavbar() {
  const [location] = useLocation();
  const [isDarkMode, setIsDarkMode] = useState(true);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  const toggleTheme = () => {
    setIsDarkMode(!isDarkMode);
    document.documentElement.classList.toggle('dark');
    console.log('Theme toggled:', isDarkMode ? 'light' : 'dark');
  };

  const toggleMobileMenu = () => {
    setMobileMenuOpen(!mobileMenuOpen);
  };

  const isActiveInGroup = (groupItems: any[]) => {
    return groupItems.some(item => location === item.url);
  };

  return (
    <nav className="sticky top-0 z-50 w-full border-b border-border bg-background/95 backdrop-blur supports-[backdrop-filter]:bg-background/60" data-testid="navbar-top">
      <div className="container-responsive">
        <div className="flex items-center justify-between h-16">
          {/* Clean Logo Section */}
          <div className="flex items-center">
            <Link href="/" className="flex items-center gap-3 hover:opacity-80 transition-opacity">
              <div className="flex items-center justify-center w-8 h-8 rounded-md bg-primary/10 border border-primary/20">
                <Shield className="w-5 h-5 text-primary" />
              </div>
              <div className="hidden sm:block">
                <h1 className="text-lg font-semibold gov-header text-foreground">BYOB-Lab</h1>
              </div>
            </Link>
          </div>

          {/* Center Navigation - Desktop */}
          <div className="hidden md:flex items-center space-x-1 flex-1 justify-center">
            {Object.entries(navigationGroups).map(([key, group]) => {
              const GroupIcon = group.icon;
              const hasActiveItem = isActiveInGroup(group.items);

              return (
                <DropdownMenu key={key}>
                  <DropdownMenuTrigger asChild>
                    <Button 
                      variant="ghost" 
                      size="sm" 
                      className={`flex items-center gap-2 font-medium transition-colors ${
                        hasActiveItem ? 'bg-primary/10 text-primary' : 'text-muted-foreground hover:text-foreground hover:bg-muted/50'
                      }`}
                      data-testid={`dropdown-${key}`}
                    >
                      <GroupIcon className="w-4 h-4" />
                      <span className="gov-body">{group.title}</span>
                      <ChevronDown className="w-3 h-3 opacity-50" />
                    </Button>
                  </DropdownMenuTrigger>
                  <DropdownMenuContent align="center" className="w-56 gov-card">
                    <DropdownMenuLabel className="gov-mono text-xs text-primary font-semibold">
                      {group.title.toUpperCase()}
                    </DropdownMenuLabel>
                    <DropdownMenuSeparator />
                    <DropdownMenuGroup>
                      {group.items.map((item) => {
                        const ItemIcon = item.icon;
                        const isActive = location === item.url;

                        return (
                          <Link key={item.url} href={item.url}>
                            <DropdownMenuItem className={`cursor-pointer transition-colors ${
                              isActive ? 'bg-primary/10 text-primary' : 'hover:bg-muted/50'
                            }`}>
                              <ItemIcon className="w-4 h-4 mr-3" />
                              <span className="gov-body">{item.title}</span>
                            </DropdownMenuItem>
                          </Link>
                        );
                      })}
                    </DropdownMenuGroup>
                  </DropdownMenuContent>
                </DropdownMenu>
              );
            })}
          </div>

          {/* Status and Controls */}
          <div className="flex items-center gap-3">
            {/* Active Sessions Count */}
            <div className="hidden lg:flex items-center gap-3 px-3 py-1 rounded-md bg-muted/20 border border-border">
              <div className="flex items-center gap-2 text-xs gov-mono">
                <div className="flex items-center gap-1">
                  <div className="w-2 h-2 rounded-full status-online"></div>
                  <span className="text-green-600 dark:text-green-400">24 ACTIVE</span>
                </div>
                <div className="w-px h-3 bg-border"></div>
                <div className="flex items-center gap-1">
                  <Zap className="w-3 h-3 text-blue-400" />
                  <span className="text-blue-400">SECURE</span>
                </div>
              </div>
            </div>

            {/* Quick Status - Mobile */}
            <div className="lg:hidden flex items-center gap-2 px-2 py-1 rounded bg-muted/20">
              <div className="w-2 h-2 rounded-full status-online"></div>
              <span className="text-xs text-green-600 dark:text-green-400 gov-mono">24</span>
            </div>

            {/* Theme Toggle */}
            <Button 
              variant="ghost" 
              size="icon" 
              onClick={toggleTheme}
              data-testid="button-theme-toggle"
              className="w-9 h-9 hover-elevate"
            >
              {isDarkMode ? <Sun className="w-4 h-4" /> : <Moon className="w-4 h-4" />}
            </Button>

            {/* Mobile Menu Toggle */}
            <Button 
              variant="ghost" 
              size="icon" 
              className="md:hidden w-9 h-9 hover-elevate"
              onClick={toggleMobileMenu}
              data-testid="button-mobile-menu"
            >
              {mobileMenuOpen ? <X className="w-4 h-4" /> : <Menu className="w-4 h-4" />}
            </Button>
          </div>
        </div>
      </div>

      {/* Professional Mobile Menu */}
      {mobileMenuOpen && (
        <div className="md:hidden border-t border-border bg-background/95 backdrop-blur supports-[backdrop-filter]:bg-background/80">
          <div className="container-responsive py-4">
            <div className="space-y-4">
              {Object.entries(navigationGroups).map(([key, group]) => (
                <div key={key} className="space-y-2">
                  <div className="flex items-center gap-2 text-xs font-semibold text-muted-foreground uppercase tracking-wider gov-mono border-l-2 border-primary/20 pl-3">
                    <group.icon className="w-3 h-3" />
                    {group.title}
                  </div>
                  <div className="pl-5 space-y-1">
                    {group.items.map((item) => {
                      const ItemIcon = item.icon;
                      const isActive = location === item.url;

                      return (
                        <Link 
                          key={item.url} 
                          href={item.url}
                          onClick={() => setMobileMenuOpen(false)}
                        >
                          <div className={`flex items-center gap-3 px-3 py-2 rounded-md text-sm transition-colors hover-elevate ${
                            isActive ? 'bg-primary/10 text-primary border border-primary/20' : 'hover:bg-muted/50 border border-transparent'
                          }`}>
                            <ItemIcon className="w-4 h-4" />
                            <span className="gov-body">{item.title}</span>
                          </div>
                        </Link>
                      );
                    })}
                  </div>
                </div>
              ))}

              {/* Mobile System Status */}
              <div className="pt-4 border-t border-border">
                <div className="text-xs font-semibold text-muted-foreground uppercase tracking-wider gov-mono mb-3">
                  System Status
                </div>
                <div className="grid grid-cols-2 gap-3">
                  <div className="flex items-center gap-2 px-3 py-2 bg-muted/20 border border-border rounded-md">
                    <div className="w-2 h-2 rounded-full status-online"></div>
                    <span className="text-xs text-green-600 dark:text-green-400 gov-mono">Active: 24</span>
                  </div>
                  <div className="flex items-center gap-2 px-3 py-2 bg-muted/20 border border-border rounded-md">
                    <div className="w-2 h-2 rounded-full status-online"></div>
                    <span className="text-xs text-green-600 dark:text-green-400 gov-mono">Secure</span>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </nav>
  );
}

